:root {
  --color-grey-light-1: #faf9f9;
  --color-grey-light-2: #f4f2f2;
  --color-grey-light-3: #f0eeee;
  --color-grey-light-4: #ccc;
  --color-grey-dark-1: #333;
  --color-grey-dark-2: #777;
  --color-grey-dark-3: #999;
  --shadow-dark: 0 2rem 6rem rgba(0, 0, 0, .3);
}

* {
  margin: 0;
  padding: 0;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
}
@media only screen and (max-width: 68.75em) {
  html {
    font-size: 50%;
  }
}

body {
  font-family: "Montserrat", sans-serif;
  font-weight: 400;
  line-height: 1.6;
  color: var(--color-grey-dark-2);
  min-height: 100vh;
}

.header {
  width: 100%;
  min-height: 100vh;
  position: relative;
}
.header__overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.8);
  z-index: -1;
}

.bg-video {
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  z-index: -2;
  opacity: 1;
  overflow: hidden;
}
.bg-video__content {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.grid__wrapper {
  width: 100%;
  height: 100%;
  padding: 4rem 5rem 0 5rem;
}
@media only screen and (max-width: 900px) {
  .grid__wrapper {
    padding: 4rem 2rem 0 2rem;
  }
}

.grid {
  width: 100%;
  min-height: calc(100vh - 8rem);
  display: grid;
  grid-template-columns: 7rem 2fr 1fr 6rem;
  grid-template-rows: repeat(6, 1fr);
}
@media only screen and (max-width: 825px) {
  .grid {
    grid-template-columns: 5rem 1fr 5rem;
    grid-template-rows: 20vh repeat(2, 1fr) 10rem;
  }
}

.text__wrapper {
  grid-column: 2/4;
  grid-row: 2/5;
  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
  flex-direction: column;
}
@media only screen and (max-width: 825px) {
  .text__wrapper {
    grid-column: 2/3;
    grid-row: 2/3;
  }
}
.text__wrapper__h1 {
  font-size: 5.2rem;
  color: #fff;
  text-transform: uppercase;
  letter-spacing: 0.3rem;
  line-height: 1;
  margin-bottom: 1rem;
}
.text__wrapper__h1--1 {
  font-weight: 800;
}
.text__wrapper__h1--2 {
  font-weight: 100;
}

.my__name {
  font-family: "Charm", cursive;
  font-size: 3rem;
  letter-spacing: 0.4rem;
  margin-top: 5rem;
}

.my__name span {
  font-family: "Montserrat", sans-serif;
  color: #fff;
  text-transform: uppercase;
  letter-spacing: 0.6rem;
  font-size: 2rem;
}

.grow {
  grid-column: 1/2;
  grid-row: 2/4;
  display: flex;
  align-items: flex-start;
  justify-content: flex-start;
}
@media only screen and (max-width: 825px) {
  .grow {
    grid-column: 1/2;
    grid-row: 2/3;
  }
}
.grow__text {
  writing-mode: vertical-rl;
  transform: rotate(180deg);
  color: #fff;
  font-size: 1.5rem;
  letter-spacing: 0.7rem;
  padding: 0;
  line-height: 1;
  text-transform: uppercase;
}

.name {
  grid-column: 1/2;
  grid-row: 4/-1;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-end;
}
@media only screen and (max-width: 825px) {
  .name {
    grid-column: 1/2;
    grid-row: 3/4;
    justify-content: flex-start;
    margin-top: 5rem;
  }
}
.name__text {
  writing-mode: vertical-rl;
  transform: rotate(180deg);
  color: #fff;
  font-size: 1.4rem;
  letter-spacing: 0.5rem;
  text-transform: capitalize;
  padding: 0;
  line-height: 1;
}

.icons__wrapper {
  grid-column: 4/-1;
  grid-row: 4/7;
}
@media only screen and (max-width: 825px) {
  .icons__wrapper {
    grid-column: 1/-1;
    grid-row: 4/-1;
    width: 100%;
  }
}

.icon__list {
  list-style: none;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-end;
}
@media only screen and (max-width: 825px) {
  .icon__list {
    grid-column: 1/-1;
    grid-row: 4/-1;
    flex-direction: row;
    width: 100%;
    align-items: center;
    justify-content: space-between;
  }
}

.user__icon {
  width: 2.5rem;
  height: 2.5rem;
  fill: #fff;
}

.buttons__wrapper {
  grid-column: 2/3;
  grid-row: 5/-1;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
}
@media only screen and (max-width: 825px) {
  .buttons__wrapper {
    grid-column: 2/3;
    grid-row: 3/4;
    justify-content: flex-start;
    align-items: stretch;
  }
}
.buttons__wrapper__h5 {
  color: #fff;
  font-size: 1.6rem;
  letter-spacing: 0.2rem;
  width: 100%;
}
@media only screen and (max-width: 825px) {
  .buttons__wrapper__h5 {
    margin-top: 5rem;
  }
}

.button__list {
  list-style: none;
  display: flex;
  width: 100%;
  align-items: center;
  justify-content: space-around;
  justify-self: center;
  margin-top: 5rem;
}
@media only screen and (max-width: 825px) {
  .button__list {
    justify-self: flex-end;
    margin-top: 15rem;
    flex-direction: column;
    justify-content: space-between;
    height: 15rem;
  }
}
.button__list__link {
  font-size: 2rem;
  color: #fff;
  text-decoration: none;
  border: 1px solid #fff;
  text-transform: uppercase;
  padding: 1rem 3rem;
  backface-visibility: hidden;
}
.button__list__link:hover {
  border: 2px solid white;
  padding: calc(1rem - 1px) calc(3rem - 1px);
}

.about__me {
  grid-column: 3/4;
  grid-row: 5/-1;
  display: flex;
  justify-content: center;
}
@media only screen and (max-width: 825px) {
  .about__me {
    grid-column: 3/4;
    grid-row: 2/3;
  }
}
.about__me__p {
  writing-mode: vertical-rl;
  text-orientation: upright;
  text-transform: uppercase;
  letter-spacing: 0.2rem;
  color: #fff;
  font-size: 1.5rem;
  font-weight: 800;
  position: relative;
  display: flex;
  align-items: center;
  padding: 0 1rem;
}
@media only screen and (max-width: 900px) {
  .about__me__p {
    justify-content: flex-end;
    padding: 0 0 0 2rem;
  }
}

.icon__item__phone {
  position: relative;
}

.icon__item__phone:hover {
  background-color: rgba(255, 255, 255, 0.2);
}

.icon__item__phone::before {
  content: "";
  position: fixed;
  top: 0;
  left: 0;
  height: 100%;
  width: 0;
  align-items: center;
  justify-content: center;
  font-size: 1.7rem;
  color: #fff;
  transition: all 0.5s ease-in-out;
  display: flex;
  backface-visibility: hidden;
}

.icon__item__phone:hover::before {
  position: absolute;
  content: "092-1987-645";
  height: 100%;
  width: 20rem;
  transform: translateX(-20rem);
  background-color: rgba(255, 255, 255, 0.2);
  overflow: hidden;
}

.icon__item__mail {
  position: relative;
}

.icon__item__mail:hover {
  background-color: rgba(255, 255, 255, 0.2);
}

.icon__item__mail::before {
  content: "";
  position: fixed;
  top: 0;
  left: 0;
  height: 100%;
  width: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.4rem;
  color: #fff;
  transition: all 0.5s ease-in-out;
}

.icon__item__mail:hover::before {
  content: "martingorupic@gmail.com";
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 20rem;
  transform: translateX(-20rem);
  background-color: rgba(255, 255, 255, 0.2);
  overflow: hidden;
}

.about__me p::before {
  position: absolute;
  top: 0;
  left: 0;
  height: 0;
  width: 0;
  content: "";
}

.about__me p:hover::before {
  height: 100%;
  width: 100%;
  background-color: rgba(255, 255, 255, 0.2);
  transition: all 0.3s linear;
}

.about__link {
  display: flex;
  flex-direction: column;
  text-decoration: none;
}

.nav {
  position: fixed;
  width: 6rem;
  height: 6rem;
  top: 4rem;
  right: 5rem;
  z-index: 10;
}
@media only screen and (max-width: 900px) {
  .nav {
    right: 2rem;
  }
}

.nav__btn {
  position: relative;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: flex-end;
  padding: 0.5rem 0;
  z-index: 20;
}

.nav__btn:hover .bar2 {
  width: 100%;
  margin: 0.5rem 0;
}

.bar {
  width: 75%;
  height: 0.4rem;
  background-color: #fff;
}

.bar1,
.bar3 {
  transition: all 0.2s linear;
}

.bar2 {
  width: 50%;
  transition: all 0.3s ease;
}

.nav__overlay__wrapper {
  position: absolute;
  top: 0.1rem;
  left: 0.1rem;
  bottom: 0.1rem;
  right: 0.1rem;
  z-index: 0;
}

.nav__overlay__wrapper.active .nav__overlay {
  transform: scale(80);
}

.nav__overlay {
  transform: scale(0);
  background: radial-gradient(ellipse at center, #4c4c4c 6%, #595959 12%, #131313 16%, #1c1c1c 20%, #2b2b2b 21%, #131313 24%, #131313 24%, #2c2c2c 25%, #2c2c2c 25%, #666666 29%, #666666 29%, #2b2b2b 33%, #2b2b2b 33%, #2b2b2b 36%, #1c1c1c 62%, #1c1c1c 62%);
  /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  position: absolute;
  top: 0.1rem;
  left: 0.1rem;
  bottom: 0.1rem;
  right: 0.1rem;
  z-index: 0;
  border-radius: 50%;
  transition: all 0.4s ease-in-out;
}

.nav__ul {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  list-style: none;
  display: none;
  opacity: 0;
  flex-direction: column;
  width: 50%;
  height: 50%;
  justify-content: space-evenly;
  align-items: center;
  transition: opacity 3s linear;
  -webkit-backface-visibility: hidden;
}

.nav__ul.active {
  display: flex;
  opacity: 1;
}

.nav__li {
  width: 80%;
  height: 6rem;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
}

.nav__li::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 0;
  background-color: var(--color-grey-light-4);
  transform: scaleY(0);
  transition: all 0.3s linear;
  -webkit-clip-path: polygon(90% 19%, 100% 34%, 100% 48%, 89% 67%, 100% 73%, 93% 89%, 100% 100%, 0 100%, 0 0, 100% 1%);
  clip-path: polygon(90% 19%, 100% 34%, 100% 48%, 89% 67%, 100% 73%, 93% 89%, 100% 100%, 0 100%, 0 0, 100% 1%);
}

.nav__li:hover::before {
  width: 100%;
  transform: scaleY(1);
}

.nav__links {
  text-decoration: none;
  font-size: 3rem;
  color: #fff;
  text-transform: uppercase;
  font-weight: 800;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  z-index: 4;
}

.toogle .bar1 {
  transform: rotate(-45deg) translateY(1rem);
}

.toogle .bar2 {
  display: none;
}

.toogle .bar3 {
  transform: rotate(45deg) translateY(-1rem);
}

@keyframes movelink {
  from {
    transform: translate(-25rem, 25rem);
    opacity: 0;
  }
  to {
    transform: translate(0, 0);
    opacity: 1;
  }
}
